import React, { useState } from "react";
import "./mcq.css";

const MCQ = () => {
  // Define the questions, options, and correct answers
  const questions = [
    {
      question:
        "An array A consists of n integers in locations A[0], A[1], ... A[n-1]. It is required to shift the elements of the array cyclically to the left by k places, where 1 ≤ k ≤ (n - 1). An incomplete algorithm for doing this in linear time, without using another array is given below. Complete the algorithm by filling in the blanks.",
      options: [
        "i > min; j != (n + i) mod n; A[j + k] = temp; i + 1;",
        "i < min; j != (n + i - k) mod n; A[(j + k) mod n] = temp; i + 1;", // Correct
        "i < min; j != (n + i + k) mod n; A[j + k] = temp; i + 1;",
        "i > min; j != (n + i + k) mod n; A[(j + k) mod n] = temp; i + 1;",
      ],
      correctAnswer:
        "i < min; j != (n + i - k) mod n; A[(j + k) mod n] = temp; i + 1;",
    },
    {
      question: "What is the time complexity of binary search in a sorted array?",
      options: ["O(n)", "O(log n)", "O(n^2)", "O(1)"],
      correctAnswer: "O(log n)",
    },
    {
      question: "Which data structure is used in a Breadth-First Search (BFS) traversal?",
      options: ["Stack", "Queue", "Priority Queue", "Linked List"],
      correctAnswer: "Queue",
    },
  ];

  // State to track the current question and the selected option
  const [answeredQuestions, setAnsweredQuestions] = useState([]);

  // Handle option selection
  const handleOptionClick = (question, option) => {
    const isAnswerCorrect = option === question.correctAnswer;

    setAnsweredQuestions((prev) => [
      ...prev.filter((q) => q.question !== question), // Update existing answer
      { question, selectedOption: option, isAnswerCorrect },
    ]);
  };

  return (
    <div>
      <div className="mcq-container">
        {questions.map((currentQuestion, questionIndex) => {
          const answered = answeredQuestions.find(
            (answer) => answer.question === currentQuestion
          );

          return (
            <div key={questionIndex} className="question">
              <p>{currentQuestion.question}</p>
              <div className="options">
                {currentQuestion.options.map((option, index) => (
                  <div
                    key={index}
                    className={`option ${
                      answered?.selectedOption === option
                        ? answered.isAnswerCorrect
                          ? "correct"
                          : "incorrect"
                        : ""
                    }`}
                    onClick={() => handleOptionClick(currentQuestion, option)}
                  >
                    <p>{option}</p>
                    {answered?.selectedOption === option && (
                      <div className="feedback">
                        {answered.isAnswerCorrect
                          ? "✅ Correct!"
                          : "❌ Incorrect, Try Again!"}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MCQ;
