import React, { useState } from 'react';
import './App.css'; // Ensure your styles are imported

const questions = [
  {
    question: "What is the capital of France?",
    options: ["Berlin", "Madrid", "Paris", "Lisbon"],
    answer: "Paris"
  },
  {
    question: "Which planet is known as the Red Planet?",
    options: ["Earth", "Mars", "Jupiter", "Saturn"],
    answer: "Mars"
  },
  {
    question: "Who wrote 'Romeo and Juliet'?",
    options: ["Mark Twain", "Charles Dickens", "William Shakespeare", "Ernest Hemingway"],
    answer: "William Shakespeare"
  },
  // Add more questions as needed
];

const MCQ = () => {
  const [selectedOption, setSelectedOption] = useState('');
  const [feedback, setFeedback] = useState('');
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  const handleSubmit = () => {
    const currentQuestion = questions[currentQuestionIndex];
    if (selectedOption === currentQuestion.answer) {
      setFeedback('Correct!');
    } else {
      setFeedback('Wrong answer. Try again!');
    }
  };

  const handleNextQuestion = () => {
    setFeedback('');
    setSelectedOption('');
    setCurrentQuestionIndex((prevIndex) => (prevIndex + 1) % questions.length);
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar" id="navbar">
        <div className="logo">EduTech</div>
        <ul className="nav-links">
          <li><a href="#home" onClick={(e) => e.preventDefault()}>Home</a></li>
          <li><a href="#courses" onClick={(e) => e.preventDefault()}>Course</a></li>
          <li><a href="#about-us" onClick={(e) => e.preventDefault()}>About Us</a></li>
          <li><a href="#mentor" onClick={(e) => e.preventDefault()}>Mentor</a></li>
          <li>
            <button className="login-button">Login</button>
          </li>
        </ul>
      </nav>

      {/* MCQ Section */}
      <div className="mcq-container">
        <h2>{questions[currentQuestionIndex].question}</h2>
        <div className="options">
          {questions[currentQuestionIndex].options.map((option, index) => (
            <div key={index} className="option">
              <input
                type="radio"
                id={option}
                name="mcq"
                value={option}
                checked={selectedOption === option}
                onChange={() => handleOptionChange(option)}
              />
              <label htmlFor={option}>{option}</label>
            </div>
          ))}
        </div>
        <button onClick={handleSubmit} className="explore-button">Submit</button>
        {feedback && <p className="feedback">{feedback}</p>}
        <button onClick={handleNextQuestion} className="explore-button">Next Question</button>
      </div>
    </div>
  );
};

export default MCQ;
