import React, { useState } from 'react';

const Mcq = ({ questions }) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [feedback, setFeedback] = useState('');

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  const handleSubmit = () => {
    // Logic for handling submission can go here
    // For example, you could check if the selected option is correct
    setFeedback('Your answer has been submitted.');
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
      setSelectedOption(null);
      setFeedback('');
    } else {
      setFeedback('This was the last question.');
    }
  };

  return (
    <div>
      {/* MCQ Section */}
      <div className="mcq-container">
        <h2>{questions[currentQuestionIndex].question}</h2>
        <div className="options">
          {questions[currentQuestionIndex].options.map((option, index) => (
            <div key={index} className="option">
              <input
                type="radio"
                id={option}
                name="mcq"
                value={option}
                checked={selectedOption === option}
                onChange={() => handleOptionChange(option)}
              />
              <label htmlFor={option}>{option}</label>
            </div>
          ))}
        </div>
        <button onClick={handleSubmit} className="explore-button">Submit</button>
        {feedback && <p className="feedback">{feedback}</p>}
        <button onClick={handleNextQuestion} className="explore-button">Next Question</button>
      </div>
    </div>
  );
};

export default Mcq;
