import React, { useState } from "react";
import "./mcq.css";

const mcq = () => {
  const questions = [
    {
      id: 1,
      question: "What is the time complexity of accessing an element in an array?",
      options: [
        "O(1)",
        "O(n)",
        "O(log n)",
        "O(n^2)",
      ],
    },
    {
      id: 2,
      question: "Which data structure is used for depth-first search?",
      options: [
        "Queue",
        "Stack",
        "Heap",
        "Graph",
      ],
    },
    // Add more questions here
  ];

  return (
    <div className="mcq-container">
      {/* Header */}
      <div className="mcq-header">
        <h1>GATE CSE: Arrays</h1>
        <div className="breadcrumb">
          Home &gt; GATE CSE &gt; Arrays
        </div>
      </div>

      {/* Questions */}
      <div className="questions-section">
        {questions.map((q) => (
          <div key={q.id} className="question-card">
            <h2 className="question-title">
              {q.id}. {q.question}
            </h2>
            <div className="options">
              {q.options.map((option, index) => (
                <div key={index} className="option">
                  <input type="radio" id={`q${q.id}_opt${index}`} name={`q${q.id}`} />
                  <label htmlFor={`q${q.id}_opt${index}`}>{option}</label>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {/* Pagination */}
      <div className="pagination">
        <button className="pagination-btn">Previous</button>
        <span className="page-number">Page 2 of 10</span>
        <button className="pagination-btn">Next</button>
      </div>
    </div>
  );
};

export default MCQPage;
