import React, { useState } from "react"; 
import "./mcq.css";

const MCQ = () => {
  const questions = [
    { 
      question: "1. An array A consists of n integers in locations A[0], A[1], ... A[n-1]. It is required to shift the elements of the array cyclically to the left by k places, where 1 ‚â§ k ‚â§ (n - 1). An incomplete algorithm for doing this in linear time, without using another array is given below. Complete the algorithm by filling in the blanks.",
       options: ["i > min; j != (n + i) mod n; A[j + k] = temp; i + 1;", "i < min; j != (n + i - k) mod n; A[(j + k) mod n] = temp; i + 1;", "i < min; j != (n + i + k) mod n; A[j + k] = temp; i + 1;", "i > min; j != (n + i + k) mod n; A[(j + k) mod n] = temp; i + 1;"], 
      correctAnswer: "i < min; j != (n + i - k) mod n; A[(j + k) mod n] = temp; i + 1;" },


     { 
      question: "2. What is the time complexity of binary search in a sorted array?",
       options: ["O(n)", "O(log n)", "O(n^2)", "O(1)"],
       correctAnswer: "O(log n)" }, { question: "3. Which data structure is used in a Breadth-First Search (BFS) traversal?", options: ["Stack", "Queue", "Priority Queue", "Linked List"], correctAnswer: "Queue" }, { question: "4. A frame buffer array is addressed in row major order for a monitor with pixel locations starting from (0,0) and ending with (100,100). What is address of the pixel(6,10)? Assume one bit storage per pixel and starting pixel location is at 0. ", options: ["1016", "1006", "610", "616"], correctAnswer: "1016" }, { question: "5. Which data structure is used in a Breadth-First Search (BFS) traversal?", options: ["Stack", "Queue", "Priority Queue", "Linked List"], correctAnswer: "Queue" }, { question: "6. A frame buffer array is addressed in row major order for a monitor with pixel locations starting from (0,0) and ending with (100,100). What is address of the pixel(6,10)? Assume one bit storage per pixel and starting pixel location is at 0. ", options: ["1016", "1006", "610", "616"], correctAnswer: "1016" }, { question: "7. Which data structure is used in a Breadth-First Search (BFS) traversal?", options: ["Stack", "Queue", "Priority Queue", "Linked List"], correctAnswer: "Queue" }, { question: "8. A frame buffer array is addressed in row major order for a monitor with pixel locations starting from (0,0) and ending with (100,100). What is address of the pixel(6,10)? Assume one bit storage per pixel and starting pixel location is at 0. ", options: ["1016", "1006", "610", "616"], correctAnswer: "1016" }, ];  
    const [currentPage, setCurrentPage] = useState(0);
    const questionsPerPage = 4;
  
    const currentQuestions = questions.slice(currentPage * questionsPerPage, (currentPage + 1) * questionsPerPage);
  
    const [answeredQuestions, setAnsweredQuestions] = useState([]);
  
    const handleOptionClick = (currentQuestion, option) => {
      const isAnswerCorrect = option === currentQuestion.correctAnswer;
      setAnsweredQuestions((prev) => [
        ...prev.filter((q) => q.question !== currentQuestion.question),
        { question: currentQuestion.question, selectedOption: option, isAnswerCorrect },
      ]);
    };
  
    const handleNextPage = () => {
      if ((currentPage + 1) * questionsPerPage < questions.length) {
        setCurrentPage((prev) => prev + 1);
      }
    };
  
    const handleSmoothScroll = (e) => {
      e.preventDefault();
      const targetId = e.target.getAttribute("href").substring(1);
      const targetElement = document.getElementById(targetId);
      targetElement.scrollIntoView({ behavior: "smooth" });
    };
  
    const togglePopup = () => {
      alert("Login popup is toggled!");
    };
  
    return (
      <>
        {/* Navbar */}
        <nav className="navbar" id="navbar">
          <div className="logo">EaseAlgo</div>
          <ul className="nav-links">
            <li>
              <a href="#home" onClick={handleSmoothScroll}>
                Home
              </a>
            </li>
            <li>
              <a href="#courses" onClick={handleSmoothScroll}>
                Course
              </a>
            </li>
            <li>
              <a href="#about-us" onClick={handleSmoothScroll}>
                About Us
              </a>
            </li>
            <li>
              <a href="#mentor" onClick={handleSmoothScroll}>
                Mentor
              </a>
            </li>
            <li>
              <button className="login-button" onClick={togglePopup}>
                Login
              </button>
            </li>
          </ul>
        </nav>
  
        {/* Header */}
        <header className="mcq-header" id="home">
          <h1>Test Your Knowledge</h1>
          <p>Answer the following questions to challenge your skills!</p>
        </header>
  
        {/* Questions */}
        <div className="mcq-container">
          {currentQuestions.map((currentQuestion, questionIndex) => {
            const answered = answeredQuestions.find(
              (answer) => answer.question === currentQuestion.question
            );
  
            return (
              <div key={questionIndex} className="question-card">
                <h3 className="question-text">{currentQuestion.question}</h3>
                <div className="options-container">
                  {currentQuestion.options.map((option, index) => (
                    <button
                      key={index}
                      className={`option-btn ${answered?.selectedOption === option
                          ? answered.isAnswerCorrect
                            ? "correct"
                            : "incorrect"
                          : ""
                        }`}
                      onClick={() => handleOptionClick(currentQuestion, option)}
                    >
                      {String.fromCharCode(65 + index)}. {option}
                    </button>
                  ))}
                </div>
                {answered?.selectedOption && (
                  <p
                    className={`feedback-message ${
                      answered.isAnswerCorrect ? "correct-feedback" : "incorrect-feedback"
                    }`}
                  >
                    {answered.isAnswerCorrect ? "Correct! üéâ" : "Oops! Try again. ‚ùå"}
                  </p>
                )}
              </div>
            );
          })}
  
          <button className="next-button" onClick={handleNextPage} disabled={(currentPage + 1) * questionsPerPage >= questions.length}>
            Next
          </button>
        </div>
  
        <div className="sidebar">
          <h3>Tips and Tricks for GATE Preparation</h3>
          <ul>
            <li>Understand the GATE syllabus thoroughly.</li>
            <li>Practice previous years' question papers.</li>
            <li>Focus on time management during exams.</li>
          </ul>
  
          <ul>
            <li>Understand the GATE syllabus thoroughly.</li>
            <li>Practice previous years' question papers.</li>
            <li>Focus on time management during exams.</li>
          </ul>
  
          <h3>Quick Formula Recap</h3>
          <ul>
            <li>Time Complexity: O(1), O(log n), O(n), etc.</li>
            <li>Sorting Algorithms: T(n) = O(n log n)</li>
            <li>Binary Search: Divide and conquer.</li>
          </ul>
  
          <ul>
            <li>Time Complexity: O(1), O(log n), O(n), etc.</li>
            <li>Sorting Algorithms: T(n) = O(n log n)</li>
            <li>Binary Search: Divide and conquer.</li>
          </ul>
  
          <h3>Highlight MCQ of the Day</h3>
          <div className="highlight-mcq">
            <p>
              <strong>{currentQuestions[0]?.question}</strong>
            </p>
          </div>
        </div>
      </>
    );
};

export default MCQ;
