import React, { useState } from "react";
import "./mcq.css";

const MCQ = () => {
  const questions = [
    {
      question:
        "1. An array A consists of n integers in locations A[0], A[1], ... A[n-1]. It is required to shift the elements of the array cyclically to the left by k places, where 1 ≤ k ≤ (n - 1). An incomplete algorithm for doing this in linear time, without using another array is given below. Complete the algorithm by filling in the blanks.",
      options: [
        "i > min; j != (n + i) mod n; A[j + k] = temp; i + 1;",
        "i < min; j != (n + i - k) mod n; A[(j + k) mod n] = temp; i + 1;", // Correct
        "i < min; j != (n + i + k) mod n; A[j + k] = temp; i + 1;",
        "i > min; j != (n + i + k) mod n; A[(j + k) mod n] = temp; i + 1;",
      ],
      correctAnswer:
        "i < min; j != (n + i - k) mod n; A[(j + k) mod n] = temp; i + 1;",
    },
    {
      question: "2. What is the time complexity of binary search in a sorted array?",
      options: ["O(n)", "O(log n)", "O(n^2)", "O(1)"],
      correctAnswer: "O(log n)",
    },
    {
      question:
        "3. Which data structure is used in a Breadth-First Search (BFS) traversal?",
      options: ["Stack", "Queue", "Priority Queue", "Linked List"],
      correctAnswer: "Queue",
    },
  ];

  const [answeredQuestions, setAnsweredQuestions] = useState([]);

  const handleOptionClick = (currentQuestion, option) => {
    const isAnswerCorrect = option === currentQuestion.correctAnswer;
    setAnsweredQuestions((prev) => [
      ...prev.filter((q) => q.question !== currentQuestion.question),
      { question: currentQuestion.question, selectedOption: option, isAnswerCorrect },
    ]);
  };

  const handleSmoothScroll = (event) => {
    event.preventDefault();
    const targetId = event.currentTarget.getAttribute("href").slice(1);
    const targetElement = document.getElementById(targetId);

    if (targetElement) {
      targetElement.scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  };

  const togglePopup = () => {
    // Handle login popup logic here
    alert("Login popup not implemented yet!");
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar" id="navbar">
        <div className="logo">EaseAlgo</div>
        <ul className="nav-links">
          <li><a href="#home" onClick={handleSmoothScroll}>Home</a></li>
          <li><a href="#courses" onClick={handleSmoothScroll}>Course</a></li>
          <li><a href="#about-us" onClick={handleSmoothScroll}>About Us</a></li>
          <li><a href="#mentor" onClick={handleSmoothScroll}>Mentor</a></li>
          <li>
            <button className="login-button" onClick={togglePopup}>Login</button>
          </li>
        </ul>
      </nav>

      {/* MCQ Questions */}
      <div className="mcq-container">
        {questions.map((currentQuestion, questionIndex) => {
          const answered = answeredQuestions.find(
            (answer) => answer.question === currentQuestion.question
          );

          return (
            <div key={questionIndex} className="question">
              <p>{currentQuestion.question}</p>
              <div className="options">
                {currentQuestion.options.map((option, index) => (
                  <div
                    key={index}
                    className={`option ${
                      answered?.selectedOption === option
                        ? answered.isAnswerCorrect
                          ? "correct"
                          : "incorrect"
                        : ""
                    }`}
                    onClick={() => handleOptionClick(currentQuestion, option)}
                  >
                    <p>{option}</p>
                    {answered?.selectedOption === option && (
                      <div className="feedback">
                        {answered.isAnswerCorrect ? "✅ Correct!" : "❌ Incorrect"}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MCQ;
