import React, { useState } from 'react';
import './mcq.css';

const MCQ = () => {
  const questions = [
    
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);

  const handleOptionClick = (option) => {
    setSelectedOption(option);
    setShowFeedback(true);
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setShowFeedback(false);
    setCurrentQuestion((prev) => prev + 1);
  };

  return (
    <div className="mcq-container">
      <h1>MCQ Quiz</h1>
      <div className="question-box">
        <h2>{questions[currentQuestion].question}</h2>
        <div className="options-container">
          {questions[currentQuestion].options.map((option, index) => (
            <button
              key={index}
              className={`option-btn ${selectedOption === option ? 'selected' : ''}`}
              onClick={() => handleOptionClick(option)}
              disabled={showFeedback} // Disable buttons after selection
            >
              {option}
            </button>
          ))}
        </div>
        {showFeedback && (
          <div
            className={`feedback ${
              selectedOption === questions[currentQuestion].answer ? 'correct' : 'incorrect'
            }`}
          >
            {selectedOption === questions[currentQuestion].answer
              ? 'Correct Answer!'
              : `Wrong Answer! The correct answer is: ${questions[currentQuestion].answer}`}
          </div>
        )}
      </div>
      <div className="navigation-buttons">
        {currentQuestion < questions.length - 1 ? (
          <button onClick={handleNextQuestion} disabled={!showFeedback}>
            Next Question
          </button>
        ) : (
          <h3>You have completed the quiz!</h3>
        )}
      </div>
    </div>
  );
};

export default MCQ;
