import React, { useState } from "react";
import "./mcq.css";

const MCQ = () => {
  const questions = [
    {
      question: "1. An array A consists of n integers in locations A[0], A[1], ... A[n-1]. It is required to shift the elements of the array cyclically to the left by k places, where 1 ‚â§ k ‚â§ (n - 1). An incomplete algorithm for doing this in linear time, without using another array is given below. Complete the algorithm by filling in the blanks.",
      options: [
        "i > min; j != (n + i) mod n; A[j + k] = temp; i + 1;",
        "i < min; j != (n + i - k) mod n; A[(j + k) mod n] = temp; i + 1;", // Correct
        "i < min; j != (n + i + k) mod n; A[j + k] = temp; i + 1;",
        "i > min; j != (n + i + k) mod n; A[(j + k) mod n] = temp; i + 1;",
      ],
      correctAnswer:
        "i < min; j != (n + i - k) mod n; A[(j + k) mod n] = temp; i + 1;",
    },
    {
      question: "2. What is the time complexity of binary search in a sorted array?",
      options: ["O(n)", "O(log n)", "O(n^2)", "O(1)"],
      correctAnswer: "O(log n)",
    },
    {
      question: "3. Which data structure is used in a Breadth-First Search (BFS) traversal?",
      options: ["Stack", "Queue", "Priority Queue", "Linked List"],
      correctAnswer: "Queue",
    },
    {
      question: "4. A frame buffer array is addressed in row major order for a monitor with pixel locations starting from (0,0) and ending with (100,100). What is address of the pixel(6,10)? Assume one bit storage per pixel and starting pixel location is at 0. ",

      options: ["1016", "1006", "610", "616"],
      correctAnswer: "1016",
    },

    {
      question: "5. A program P reads in 500 integers in the range [0,100] representing the scores of 500 students. It then prints the frequency of each score above 50. What would be the best way for P to store the frequencies? ",

      options: [
        "An array of 500 numbers;",
        "An array of 100 numbers", 
        "An array of 50 numbers;",
        "A dynamically allocated array of 550 numbers;",
      ],
      correctAnswer: "An array of 50 numbers;",
    },
    // Additional questions...
  ];

  const tipsAndTricks = [
    "Revise key topics like Data Structures, Algorithms, and Mathematics daily.",
    "Solve previous years' GATE question papers to get a sense of the exam pattern.",
    "Time yourself while solving questions to improve speed and accuracy.",
  ];

  const formulaRecap = [
    "Time Complexity of Binary Search: O(log n)",
    "Cyclic Array Rotation Formula: New Index = (i + k) % n",
    "Area of a Circle: œÄr¬≤",
  ];

  const highlightMCQ = {
    question: "What is the space complexity of DFS using an adjacency list?",
    options: ["O(V)", "O(E)", "O(V + E)", "O(1)"],
    correctAnswer: "O(V)",
  };

  const [answeredQuestions, setAnsweredQuestions] = useState([]);

  const handleOptionClick = (currentQuestion, option) => {
    const isAnswerCorrect = option === currentQuestion.correctAnswer;
    setAnsweredQuestions((prev) => [
      ...prev.filter((q) => q.question !== currentQuestion.question),
      { question: currentQuestion.question, selectedOption: option, isAnswerCorrect },
    ]);
  };

  return (
    <div className="mcq-page">
      {/* Main Content */}
      <div className="mcq-content">
        <header className="mcq-header">
          <h1>Test Your Knowledge</h1>
          <p>Answer the following questions to challenge your skills!</p>
        </header>

        <div className="mcq-container">
          {questions.map((currentQuestion, questionIndex) => {
            const answered = answeredQuestions.find(
              (answer) => answer.question === currentQuestion.question
            );

            return (
              <div key={questionIndex} className="question-card">
                <h3 className="question-text">{currentQuestion.question}</h3>
                <div className="options-container">
                  {currentQuestion.options.map((option, index) => (
                    <button
                      key={index}
                      className={`option-btn ${
                        answered?.selectedOption === option
                          ? answered.isAnswerCorrect
                            ? "correct"
                            : "incorrect"
                          : ""
                      }`}
                      onClick={() => handleOptionClick(currentQuestion, option)}
                    >
                      {String.fromCharCode(65 + index)}. {option}
                    </button>
                  ))}
                </div>
                {answered?.selectedOption && (
                  <p
                    className={`feedback-message ${
                      answered.isAnswerCorrect ? "correct-feedback" : "incorrect-feedback"
                    }`}
                  >
                    {answered.isAnswerCorrect ? "Correct! üéâ" : "Oops! Try again. ‚ùå"}
                  </p>
                )}
              </div>
            );
          })}
        </div>
      </div>

      {/* Sidebar */}
      <div className="mcq-sidebar">
        <div className="tips-tricks">
          <h3>Tips & Tricks for GATE Preparation</h3>
          <ul>
            {tipsAndTricks.map((tip, index) => (
              <li key={index}>{tip}</li>
            ))}
          </ul>
        </div>

        <div className="quick-formula-recap">
          <h3>Quick Formula Recap</h3>
          <ul>
            {formulaRecap.map((formula, index) => (
              <li key={index}>{formula}</li>
            ))}
          </ul>
        </div>

        <div className="highlight-mcq">
          <h3>MCQ of the Day</h3>
          <p><strong>{highlightMCQ.question}</strong></p>
          <ul>
            {highlightMCQ.options.map((option, index) => (
              <li key={index}>{String.fromCharCode(65 + index)}. {option}</li>
            ))}
          </ul>
          <p><strong>Correct Answer: </strong>{highlightMCQ.correctAnswer}</p>
        </div>
      </div>
    </div>
  );
};

export default MCQ;
